UPDATE `quest_template` SET `RequestItemsText`='$n, now is no time for idle chatter.  If you still wish to prove yourself to The People\'s Militia you need to slay the Defias I notified you about earlier.  Return to me when you have completed your duty.' WHERE `entry`=14;
